interface MenuCategory {
  id: number;
  name: string;
  description: string;
  display_order: number;
  site: string;
}

interface MenuItem {
  id: number;
  name: string;
  description: string;
  price: number;
  category_id: number;
  image_url?: string;
  is_available: boolean;
  is_daily_special: boolean;
  site: string;
