              <Label htmlFor="daily_special" className="text-sm font-medium">Daily Special</Label>
            </div>
          </div>
        </div>

        <div className="flex justify-end space-x-2 pt-6">
          <Button 
            variant="outline" 
            onClick={onClose}
            className="border-gray-300 text-gray-700 hover:bg-gray-50"
          >
