            <EditUserForm 
              user={selectedUser} 
              onClose={() => {
                setEditUserDialog(false);
                setSelectedUser(null);
              }} 
            />
          )}
        </DialogContent>
      </Dialog>

      {/* Edit Organization Dialog */}
      <Dialog open={editOrgDialog} onOpenChange={setEditOrgDialog}>
        <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto">
          <DialogHeader>
            <DialogTitle>Edit Organization</DialogTitle>
          </DialogHeader>
          {selectedOrg && (
            <EditOrganizationForm 
              organization={selectedOrg} 
              onClose={() => {
                setEditOrgDialog(false);
                setSelectedOrg(null);
              }} 
            />
          )}
        </DialogContent>
      </Dialog>

      {/* Edit Menu Item Dialog */}
      <Dialog open={editMenuItemDialog} onOpenChange={setEditMenuItemDialog}>
        <DialogContent className="max-w-md">
          <DialogHeader>
            <DialogTitle>Edit Menu Item</DialogTitle>
          </DialogHeader>
          {selectedMenuItem && (
            <SimpleEditForm 
              item={selectedMenuItem} 
              onClose={() => {
                setEditMenuItemDialog(false);
                setSelectedMenuItem(null);
              }} 
            />
          )}
        </DialogContent>
      </Dialog>

      {/* Edit Room Dialog */}
      <Dialog open={editRoomDialog} onOpenChange={setEditRoomDialog}>
        <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto">
          <DialogHeader>
            <DialogTitle>Edit Meeting Room</DialogTitle>
          </DialogHeader>
          {selectedRoom && (
            <EditRoomForm 
              room={selectedRoom} 
              onClose={() => {
                setEditRoomDialog(false);
                setSelectedRoom(null);
              }} 
