          )}
        </DialogContent>
      </Dialog>

      {/* Edit Announcement Dialog */}
      <Dialog open={editAnnouncementDialog} onOpenChange={setEditAnnouncementDialog}>
        <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto">
          <DialogHeader>
            <DialogTitle>Edit Announcement</DialogTitle>
          </DialogHeader>
          {selectedAnnouncement && (
            <EditAnnouncementForm 
              announcement={selectedAnnouncement} 
              onClose={() => {
                setEditAnnouncementDialog(false);
                setSelectedAnnouncement(null);
              }} 
            />
          )}
        </DialogContent>
      </Dialog>

      {/* Delete User Confirmation Dialog */}
      <Dialog open={deleteUserDialog} onOpenChange={setDeleteUserDialog}>
        <DialogContent className="max-w-md">
          <DialogHeader>
            <DialogTitle className="flex items-center gap-2 text-red-600">
              <Trash2 className="h-5 w-5" />
              Delete User Confirmation
            </DialogTitle>
          </DialogHeader>
          <div className="space-y-4">
            <p className="text-sm text-gray-600">
              Are you sure you want to delete user "<strong>{userToDelete?.name}</strong>"? 
              This action cannot be undone and will permanently remove:
            </p>
            <ul className="text-sm text-gray-600 space-y-1 ml-4">
              <li>‚Ä¢ User account and profile</li>
              <li>‚Ä¢ All cafe orders and order history</li>
              <li>‚Ä¢ All meeting room bookings</li>
              <li>‚Ä¢ Community profile and interactions</li>
            </ul>
          </div>
          <DialogFooter className="gap-2">
            <Button
              variant="outline"
              onClick={() => {
                setDeleteUserDialog(false);
                setUserToDelete(null);
              }}
            >
              Cancel
            </Button>
            <Button
              variant="destructive"
              onClick={confirmDeleteUser}
              disabled={deleteUserMutation.isPending}
            >
              {deleteUserMutation.isPending ? "Deleting..." : "Yes, Delete User"}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      {/* Delete Organization Confirmation Dialog */}
      <Dialog open={deleteOrgDialog} onOpenChange={setDeleteOrgDialog}>
        <DialogContent className="max-w-md">
          <DialogHeader>
            <DialogTitle className="flex items-center gap-2 text-red-600">
              <Trash2 className="h-5 w-5" />
              Delete Organization Confirmation
            </DialogTitle>
          </DialogHeader>
          <div className="space-y-4">
            <p className="text-sm text-gray-600">
              Are you sure you want to delete organization "<strong>{orgToDelete?.name}</strong>"? 
              This action cannot be undone and will permanently remove:
            </p>
            <ul className="text-sm text-gray-600 space-y-1 ml-4">
              <li>‚Ä¢ Organization account and settings</li>
              <li>‚Ä¢ All associated users and their data</li>
              <li>‚Ä¢ All cafe orders from organization members</li>
              <li>‚Ä¢ All meeting room bookings</li>
              <li>‚Ä¢ Billing history and invoices</li>
            </ul>
          </div>
          <DialogFooter className="gap-2">
            <Button
              variant="outline"
              onClick={() => {
                setDeleteOrgDialog(false);
                setOrgToDelete(null);
              }}
            >
              Cancel
            </Button>
            <Button
              variant="destructive"
              onClick={confirmDeleteOrganization}
              disabled={deleteOrganizationMutation.isPending}
            >
              {deleteOrganizationMutation.isPending ? "Deleting..." : "Yes, Delete Organization"}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  );
}

