client/src/components/pwa/notification-s.content:
import { useState } from 'react';
import { usePWA } from '@/hooks/use-pwa';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Bell, BellOff, X } from 'lucide-react';
import { useToast } from '@/hooks/use-toast';

export function NotificationSetup() {
  const { notificationPermission, requestNotificationPermission, subscribeToPushNotifications } = usePWA();
  const [dismissed, setDismissed] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const { toast } = useToast();

  if (notificationPermission === 'granted' || notificationPermission === 'denied' || dismissed) {
    return null;
  }

  const handleEnableNotifications = async () => {
    setIsLoading(true);
    try {
      const permission = await requestNotificationPermission();

      if (permission === 'granted') {
        await subscribeToPushNotifications();
        toast({
          title: "Notifications Enabled",
          description: "You'll receive updates about your orders and bookings.",
        });
        setDismissed(true);
      } else {
        toast({
          title: "Notifications Blocked",
          description: "You can enable notifications in your browser settings.",
          variant: "destructive",
        });
      }
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to enable notifications. Please try again.",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <Card className="fixed bottom-4 left-4 w-80 z-50 shadow-lg border-blue-200">
      <CardHeader className="pb-3">
        <div className="flex items-center justify-between">
          <div className="flex items-center gap-2">
            <Bell className="h-5 w-5 text-blue-600" />
            <CardTitle className="text-sm">Enable Notifications</CardTitle>
          </div>
          <Button
            variant="ghost"
            size="sm"
            onClick={() => setDismissed(true)}
            className="h-6 w-6 p-0"
          >
            <X className="h-4 w-4" />
          </Button>
        </div>
        <CardDescription className="text-xs">
          Get real-time updates about your cafe orders and room bookings
        </CardDescription>
      </CardHeader>
      <CardContent className="pt-0">
        <div className="flex gap-2">
          <Button
            onClick={handleEnableNotifications}
            disabled={isLoading}
            className="flex-1 h-9 text-sm"
            size="sm"
          >
            {isLoading ? (
              <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
            ) : (
              <Bell className="h-4 w-4 mr-2" />
            )}
            Enable
          </Button>
          <Button
            variant="outline"
            onClick={() => setDismissed(true)}
            className="h-9 text-sm"
            size="sm"
          >
            <BellOff className="h-4 w-4 mr-2" />
            Skip
          </Button>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/rooms/booking-moda.content:
import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { useAuth } from "@/hooks/use-auth";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Badge } from "@/components/ui/badge";
import { Users, Coins } from "lucide-react";
import { MeetingRoom } from "@/lib/types";

interface BookingModalProps {
  room: MeetingRoom | null;
  bookingData: {
    date: string;
    start_time: string;
    duration: string;
  };
  onClose: () => void;
}

export default function BookingModal({ room, bookingData, onClose }: BookingModalProps) {
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [billingType, setBillingType] = useState<"personal" | "organization">("personal");
  const [notes, setNotes] = useState("");

  const calculateCreditsNeeded = () => {
    if (!room) return 0;
    // Fixed credit calculation: 1 hour = 1 credit, 30 min = 0.5 credits
    return parseFloat(bookingData.duration);
  };

  const calculateEndTime = () => {
    if (!bookingData.date || !bookingData.start_time) return "";

    const startDateTime = new Date(`${bookingData.date}T${bookingData.start_time}`);
    const endDateTime = new Date(startDateTime.getTime() + (parseFloat(bookingData.duration) * 60 * 60 * 1000));

    return endDateTime.toISOString();
  };

  const bookRoomMutation = useMutation({
    mutationFn: async (bookingDetails: any) => {
      const response = await apiRequest("POST", "/api/bookings", bookingDetails);
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Room Booked",
        description: "Your meeting room has been booked successfully",
      });
      onClose();
      queryClient.invalidateQueries({ queryKey: ["/api/bookings"] });
      queryClient.invalidateQueries({ queryKey: ["/api/auth/me"] });
    },
    onError: (error: any) => {
      toast({
        title: "Booking Failed",
        description: error.message || "Failed to book room",
        variant: "destructive",
      });
    },
  });

  const handleBookRoom = () => {
    if (!room || !user) return;

    const creditsNeeded = calculateCreditsNeeded();
    const availableCredits = user.credits - user.used_credits;

    if (creditsNeeded > availableCredits) {
      toast({
        title: "Insufficient Credits",
        description: `You need ${creditsNeeded} credits but only have ${availableCredits} available`,
        variant: "destructive",
      });
      return;
    }

    const startDateTime = new Date(`${bookingData.date}T${bookingData.start_time}`);
    const endDateTime = calculateEndTime();

    const bookingDetails = {
      room_id: room.id,
      start_time: startDateTime.toISOString(),
      end_time: endDateTime,
      billed_to: billingType,
      notes,
    };

    bookRoomMutation.mutate(bookingDetails);
  };

  if (!room) return null;

  const creditsNeeded = calculateCreditsNeeded();
  const availableCredits = user ? user.credits - user.used_credits : 0;

  return (
    <Dialog open={!!room} onOpenChange={onClose}>
      <DialogContent className="max-w-md">
        <DialogHeader>
          <DialogTitle>Book {room.name}</DialogTitle>
        </DialogHeader>

        <div className="space-y-6">
          {/* Room Details */}
          <div>
            <h3 className="font-medium mb-3">Room Details</h3>
            <div className="space-y-2">
              <div className="flex items-center space-x-2">
                <Users className="h-4 w-4 text-gray-400" />
                <span className="text-sm">Capacity: {room.capacity} people</span>
              </div>
              <div className="flex items-center space-x-2">
                <Coins className="h-4 w-4 text-gray-400" />
                <span className="text-sm">Cost: {room.credit_cost_per_hour} credits/hour</span>
              </div>
            </div>

            {room.amenities && room.amenities.length > 0 && (
              <div className="mt-3">
                <p className="text-sm font-medium mb-2">Amenities:</p>
                <div className="flex flex-wrap gap-1">
                  {room.amenities.map((amenity) => (
                    <Badge key={amenity} variant="secondary" className="text-xs">
                      {amenity}
                    </Badge>
                  ))}
                </div>
              </div>
            )}
          </div>

          {/* Booking Summary */}
          <div>
            <h3 className="font-medium mb-3">Booking Summary</h3>
            <div className="bg-gray-50 p-3 rounded-lg space-y-2">
              <div className="flex justify-between">
                <span className="text-sm">Date:</span>
                <span className="text-sm font-medium">
                  {new Date(bookingData.date).toLocaleDateString('en-GB', { 
                    day: '2-digit', 
                    month: '2-digit', 
                    year: 'numeric' 
                  })}
                </span>
              </div>
              <div className="flex justify-between">
                <span className="text-sm">Time:</span>
                <span className="text-sm font-medium">
                  {bookingData.start_time} ({bookingData.duration}h)
                </span>
              </div>
              <div className="flex justify-between">
                <span className="text-sm">Credits needed:</span>
                <span className="text-sm font-medium">{creditsNeeded}</span>
              </div>
              <div className="flex justify-between">
                <span className="text-sm">Available credits:</span>
                <span className={`text-sm font-medium ${availableCredits >= creditsNeeded ? 'text-green-600' : 'text-red-600'}`}>
                  {availableCredits}
                </span>
              </div>
            </div>
          </div>

          {/* Billing Toggle */}
          {user?.can_charge_room_to_org && (
            <div>
              <Label className="text-sm font-medium mb-2 block">Bill to:</Label>
              <RadioGroup value={billingType} onValueChange={setBillingType as any}>
                <div className="flex items-center space-x-2">
                  <RadioGroupItem value="personal" id="personal" />
                  <Label htmlFor="personal">Personal</Label>
                </div>
                <div className="flex items-center space-x-2">
                  <RadioGroupItem value="organization" id="organization" />
                  <Label htmlFor="organization">My Company</Label>
                </div>
              </RadioGroup>
            </div>
          )}

          {/* Notes */}
          <div>
            <Label htmlFor="notes" className="text-sm font-medium mb-2 block">
              Notes (Optional)
            </Label>
            <Textarea
              id="notes"
              value={notes}
              onChange={(e) => setNotes(e.target.value)}
              placeholder="Meeting purpose, special requirements..."
            />
          </div>

          <div className="flex space-x-2">
            <Button
              variant="outline"
              onClick={onClose}
              className="flex-1"
            >
              Cancel
            </Button>
            <Button
              onClick={handleBookRoom}
              disabled={bookRoomMutation.isPending || creditsNeeded > availableCredits}
              className="flex-1"
            >
              {bookRoomMutation.isPending ? "Booking..." : "Book Room"}
            </Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/rooms/room-card.ts.content:
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Users, Tv, Wifi, Calendar } from "lucide-react";

interface RoomCardProps {
  room: {
    id: number;
    name: string;
    description?: string;
    capacity: number;
    credit_cost_per_hour: number;
    amenities?: string[];
    image_url?: string;
    is_available: boolean;
  };
  onBook: (room: any) => void;
  bookingData: {
    date: string;
    start_time: string;
    duration: string;
  };
}

export default function RoomCard({ room, onBook, bookingData }: RoomCardProps) {
  const isBookingDataComplete = bookingData.date && bookingData.start_time && bookingData.duration;

  const amenityIcons: { [key: string]: any } = {
    "TV": Tv,
    "Display": Tv,
    "Projector": Tv,
    "WiFi": Wifi,
    "Whiteboard": Calendar,
    "Sound System": Tv,
  };

  return (
    <Card className="overflow-hidden hover:shadow-md transition-shadow">
      <div className="relative">
        {room.image_url ? (
          <img
            src={room.image_url}
            alt={room.name}
            className="w-full h-48 object-cover"
          />
        ) : (
          <div className="w-full h-48 bg-gray-200 flex items-center justify-center">
            <Calendar className="h-12 w-12 text-gray-400" />
          </div>
        )}
      </div>

      <CardContent className="p-6">
        <div className="flex items-center justify-between mb-2">
          <h3 className="text-lg font-semibold text-gray-900">{room.name}</h3>
          <span className="text-sm font-medium text-primary">
            {room.credit_cost_per_hour} Credits/hr
          </span>
        </div>

        <div className="flex items-center space-x-4 mb-4">
          <div className="flex items-center space-x-1">
            <Users className="h-4 w-4 text-gray-400" />
            <span className="text-sm text-gray-600">{room.capacity} people</span>
          </div>
        </div>

        {room.amenities && room.amenities.length > 0 && (
          <div className="flex flex-wrap gap-2 mb-4">
            {room.amenities.map((amenity) => {
              const Icon = amenityIcons[amenity] || Calendar;
              return (
                <Badge key={amenity} variant="secondary" className="text-xs">
                  <Icon className="h-3 w-3 mr-1" />
                  {amenity}
                </Badge>
              );
            })}
          </div>
        )}

        <div className="flex items-center justify-between">
          <span className={`text-sm font-medium ${room.is_available ? 'text-green-600' : 'text-red-600'}`}>
            {room.is_available ? 'Available' : 'Unavailable'}
          </span>
          <Button
            onClick={() => onBook(room)}
            disabled={!room.is_available || !isBookingDataComplete}
            className={room.is_available && isBookingDataComplete ? 'bg-primary hover:bg-blue-700' : ''}
          >
            {room.is_available ? 'Book Now' : 'Unavailable'}
          </Button>
        </div>
      </CardContent>
    </Card>
  );
}

