console.log('\nüñ•Ô∏è  SYSTEM RESOURCES:');
try {
  const memInfo = execSync('free -m', { encoding: 'utf8' });
  const memLines = memInfo.split('\n')[1].split(/\s+/);
  const totalMem = parseInt(memLines[1]);
  const usedMem = parseInt(memLines[2]);
