  console.log(`   System Memory: ${usedMem}MB / ${totalMem}MB (${memUsagePercent}%)`);
  console.log(`   Memory Status: ${memUsagePercent > 85 ? 'üî¥ HIGH' : memUsagePercent > 70 ? 'üü° MEDIUM' : 'üü¢ NORMAL'}`);
